/*! minimal Flowbite stub for modal toggle */
(function(){
  function ready(fn){ if(document.readyState!='loading'){fn()} else {document.addEventListener('DOMContentLoaded', fn)} }
  ready(function(){
    document.querySelectorAll('[data-modal-toggle]').forEach(function(btn){
      var targetId = btn.getAttribute('data-modal-target') || btn.getAttribute('data-modal-toggle');
      var modal = document.getElementById(targetId);
      if(!modal) return;
      function show(){ modal.classList.remove('hidden') }
      function hide(){ modal.classList.add('hidden') }
      btn.addEventListener('click', show);
      modal.querySelectorAll('[data-modal-hide]').forEach(function(h){ h.addEventListener('click', hide) })
    })
  })
})();